(function() {
  var Container, MapReader, Rectangle, Sprite, Stats, Texture, height, loader, name, stats, styleNode, tau, width, _ref;

  global.require = require;

  global.PACKAGE = PACKAGE;

  styleNode = document.createElement("style");

  styleNode.innerHTML = require('./style');

  document.head.appendChild(styleNode);

  Stats = require("./lib/stats.min");

  stats = new Stats;

  document.body.appendChild(stats.dom);

  MapReader = require("./lib/map-reader");

  _ref = require("./pixie"), width = _ref.width, height = _ref.height, name = _ref.name;

  tau = 2 * Math.PI;

  loader = PIXI.loader, Container = PIXI.Container, Rectangle = PIXI.Rectangle, Sprite = PIXI.Sprite, Texture = PIXI.Texture;

  loader.add([
    {
      name: "pika",
      url: "https://2.pixiecdn.com/sprites/137922/original.png?1"
    }, {
      name: "sheet",
      url: "https://danielx.whimsy.space/axe-blaster/platformertiles.png"
    }, {
      name: "map",
      url: "https://danielx.whimsy.space/axe-blaster/map.png"
    }
  ]).load(function() {
    var blockTexture, gameLoop, mapData, renderer, sprite, stage, texture, update;
    renderer = PIXI.autoDetectRenderer(width, height, {
      antialias: false,
      transparent: false,
      resolution: 1
    });
    document.body.appendChild(renderer.view);
    stage = new Container();
    blockTexture = new Texture(loader.resources["sheet"].texture, new Rectangle(32, 32, 32, 32));
    mapData = MapReader(loader.resources.map.texture.baseTexture.source);
    (function(_arg) {
      var data, height, width;
      data = _arg.data, width = _arg.width, height = _arg.height;
      return data.forEach(function(value, i) {
        var block, x, y;
        x = i % width;
        y = (i / width) | 0;
        if (value) {
          block = new Sprite(blockTexture);
          block.x = x * 32;
          block.y = y * 32;
          return stage.addChild(block);
        }
      });
    })(mapData);
    texture = loader.resources["pika"].texture;
    sprite = new Sprite(texture);
    stage.addChild(sprite);
    sprite.x = width / 2;
    sprite.y = height / 2;
    sprite.scale.set(2);
    sprite.anchor.set(0.5);
    sprite.rotation = 0.5 * tau;
    update = function() {
      return sprite.x += 1;
    };
    gameLoop = function() {
      requestAnimationFrame(gameLoop);
      stats.begin();
      update();
      renderer.render(stage);
      return stats.end();
    };
    return gameLoop();
  });

}).call(this);

