  var Stats, gameLoop, stats;

  Stats = require("../lib/stats.min");

  stats = new Stats;

  document.body.appendChild(stats.dom);

  gameLoop = function() {
    requestAnimationFrame(gameLoop);
    stats.begin();
    return stats.end();
  };

  gameLoop();

}).call(this);
