/*
Create the renderer and game loop.
Append stats and things to the dom

Calls the active scene's update method and renders it in the PIXI renderer.
 */

(function() {
  var Container, Point, Rectangle, Sprite, Text, Texture, loader, nullScene;

  loader = PIXI.loader, Container = PIXI.Container, Point = PIXI.Point, Rectangle = PIXI.Rectangle, Sprite = PIXI.Sprite, Text = PIXI.Text, Texture = PIXI.Texture;

  nullScene = new Container;

  nullScene.update = function() {};

  module.exports = function(_arg) {
    var activeScene, contentElement, debugText, dt, gameLoop, height, nextScene, renderer, self, width;
    width = _arg.width, height = _arg.height;
    renderer = PIXI.autoDetectRenderer(width, height, {
      antialias: false,
      transparent: false,
      resolution: 1
    });
    contentElement = document.createElement("content");
    debugText = document.createElement("pre");
    debugText.classList.add("debug");
    contentElement.appendChild(debugText);
    contentElement.appendChild(renderer.view);
    activeScene = nullScene;
    nextScene = activeScene;
    self = {
      element: contentElement,
      renderer: renderer,
      setScene: function(scene) {
        return nextScene = scene;
      },
      debugText: function(text) {
        return debugText.textContent = text;
      }
    };
    dt = 1 / 60;
    gameLoop = function() {
      requestAnimationFrame(gameLoop);
      stats.begin();
      if (nextScene !== activeScene) {
        activeScene.emit("exited");
        activeScene = nextScene;
        activeScene.emit("entered");
      }
      activeScene.update(dt, self);
      renderer.render(activeScene);
      return stats.end();
    };
    gameLoop();
    return self;
  };

}).call(this);

