    player.scale.set(0.5);
    player.anchor.set(0.5);
    updateInput = function(dt) {
      var acc, ratio;
      movementX = 0;
      if (!keydown("ArrowUp")) {
        if (!jumpReleased) {
          if (velocity.y <= -900) {
            velocity.y += 750;
          }
        }
        jumpReleased = true;
      }
      if (keydown("ArrowLeft")) {
        lastDirection = movementX = -1;
      }
      if (keydown("ArrowRight")) {
        lastDirection = movementX = +1;
      }
      fastFall = keydown("ArrowDown") || 0;
      if (keydown("ArrowUp") && lastJumping >= 0.25 && jumpReleased) {
        jumpReleased = false;
        lastJumping = 0;
        velocity.x += movementX * jumpDirectionalImpulse;
        if (jumpSound.playing) {
          jumpSound.stop();
        }
        jumpSound.play();
        if (lastStanding <= 0.1) {
          jumpCount = 1;
          velocity.y = jumpImpulse;
        } else if (jumpCount < 2) {
          ratio = (5 - jumpCount) / 6;
          velocity.y = ratio * jumpImpulse;
          jumpCount += 1;
        }
      } else {
        lastJumping += dt;
      }
      acc = movementAcceleration;
      if (movementX === -sign(velocity.x)) {
        return velocity.x += 2 * movementX * acc * dt;
      } else {
        return velocity.x += movementX * acc * dt;
      }
    };
    player.update = function(dt, collisionGeometry) {
      var dx, dy, friction, n, signX, signY;
      updateInput(dt);
      if (!movementX) {
        if (standing) {
          friction = groundFriction;
        } else {
          friction = airFriction;
        }
      } else {
        friction = 0;
      }
      velocity.x = approach(velocity.x, 0, friction * dt);
      velocity.y += gravity * (fastFall + 1) * dt;
      if (velocity.x > maxVelocityX) {
        velocity.x = maxVelocityX;
      }
      if (velocity.x < -maxVelocityX) {
        velocity.x = -maxVelocityX;
      }
      if (velocity.y > maxVelocityY) {
        velocity.y = maxVelocityY;
      }
      if (velocity.y < -maxVelocityY) {
        velocity.y = -maxVelocityY;
      }
      dx = velocity.x * dt + residual.x;
      dy = velocity.y * dt + residual.y;
      bounds.x = player.x;
      bounds.y = player.y;
      bounds.width = player.width;
      bounds.height = player.height;
      signX = sign(dx);
      residual.x = dx - (dx | 0);
      n = abs(dx | 0);
      while (n > 0) {
        n--;
        bounds.x += signX;
        if (!collides(bounds, collisionGeometry)) {
          player.x += signX;
        } else {
          velocity.x = 0;
          residual.x = 0;
        }
      }
      bounds.x = player.x;
      signY = sign(dy);
      residual.y = dy - (dy | 0);
      n = abs(dy | 0);
      while (n > 0) {
        n--;
        bounds.y += signY;
        if (!collides(bounds, collisionGeometry)) {
          player.y += signY;
        } else {
          velocity.y = 0;
          residual.y = 0;
        }
      }
      bounds.y = player.y + 1;
      standing = collides(bounds, collisionGeometry);
      if (standing) {
        lastStanding = 0;
        jumpCount = 0;
      } else {
        lastStanding += dt;
      }
      return player.scale.x = -1 / 2 * lastDirection;
    };
    Object.assign(player, {
      bounds: bounds,
      velocity: velocity
    });
    return player;
  };

  getTileBounds = function(tile, rect) {
    rect.x = tile.x + tile.width / 2;
    rect.y = tile.y + tile.height / 2;
    rect.width = tile.width;
    rect.height = tile.height;
    return rect;
  };

  testRect = new Rectangle;

  collides = function(bounds, objects) {
    var i, length, result;
    i = 0;
    length = objects.length;
    while (i < length) {
      result = hitTestRectangle2(bounds, getTileBounds(objects[i], testRect));
      if (result) {
        return result;
      }
      i += 1;
    }
  };

  approach = function(value, target, maxDelta) {
    return value + clamp(target - value, -maxDelta, maxDelta);
  };

}).call(this);

scenes/intro.content:
/*
An intro that presents the following title cards:

- Whimsy.Space Games

- in association with
  Daniel X. Moore
 */

(function() {
  var Container, Graphics, Point, Rectangle, Sprite, Text, Texture, assocText, bg, height, letterSprites, loader, max, min, stage, timings, toLetters, whiteBg, width, _ref;

  _ref = require("../pixie"), width = _ref.width, height = _ref.height;

  Container = PIXI.Container, Graphics = PIXI.Graphics, Point = PIXI.Point, Rectangle = PIXI.Rectangle, Sprite = PIXI.Sprite, Text = PIXI.Text, Texture = PIXI.Texture;

  loader = new PIXI.loaders.Loader;

  min = Math.min, max = Math.max;

  stage = new Container();

  whiteBg = new Graphics();

  whiteBg.beginFill(0xFFFFFF);

  whiteBg.drawRect(100, 100, width, height);

  stage.addChild(whiteBg);

  bg = new Graphics();

  bg.beginFill(0x000000);

  bg.drawRect(0, 0, width, height);

  bg.alpha = 0;

  stage.addChild(bg);

  toLetters = function(letters) {
    return letters.split("").map(function(letter) {
      return new Text(letter, {
        align: "center",
        fill: "black",
        fontSize: "128px",
        fontFamily: "courier"
      });
    });
  };

  letterSprites = toLetters("Whimsy.Space").map(function(text, i) {
    var shift;
    shift = (i * 5 + 5) % 12 - 128;
    text.x = (i + 3) * width / 18;
    text.y = height / 2 + shift;
    text.visible = true;
    stage.addChild(text);
    return text;
  }).concat(toLetters("Games").map(function(text, i) {
    var shift;
    shift = (i * 5 + 7) % 12;
    text.x = (i + 6.5) * width / 18;
    text.y = height / 2 + shift;
    text.visible = true;
    stage.addChild(text);
    return text;
  }));

  assocText = (function() {
    var text;
    text = new Text("in association with\n\nDANIEL X. MOORE", {
      align: "center",
      fill: "white",
      fontFamily: "helvetica",
      fontSize: "64px",
      fontStyle: "bold italic"
    });
    text.alpha = 0;
    text.anchor.set(0.5);
    text.x = width / 2;
    text.y = height / 2;
    stage.addChild(text);
    return text;
  })();

  timings = [0.063, 0.189, 0.333, 0.578, 0.817, 1.091, 1.448, 1.747, 1.925, 2.131, 2.308, 2.472, 3.765, 3.998, 4.153, 4.330, 4.728];

  module.exports = function() {
    var dt, loaded, t;
    stage.update = function(dt) {
      if (t >= 10) {
        stage.emit('complete');
      }
      letterSprites.forEach(function(letter, i) {
        return letter.visible = t > timings[i];
      });
      bg.alpha = max((t - 5.25) / 0.875, 0);
      assocText.alpha = max((t - 6.5) / 0.875, 0);
      if (t > 8.375) {
        assocText.alpha = 1 - max((t - 8.375) / 0.875, 0);
      }
      if (loaded) {
        return t += dt;
      }
    };
    dt = 1 / 60;
    t = 0;
    loaded = false;
    loader.add([
      {
        name: "typing",
        url: "https://danielx.whimsy.space/axe-blaster/type.ogg"
      }
    ]).load(function() {
      loaded = true;
      return loader.resources.typing.data.play();
    });
    return stage;
  };

}).call(this);

scenes/platformer.content:
(function() {
  var Container, MapChunk, MapReader, Panzoom, Player, height, loader, playAudio, width, _ref;

  _ref = require("../pixie"), width = _ref.width, height = _ref.height;

  Container = PIXI.Container;

  MapReader = require("../lib/map-reader");

  MapChunk = require("../models/map-chunk");

  Player = require("../models/player");

  Panzoom = require("../lib/panzoom");

  loader = new PIXI.loaders.Loader;

  playAudio = function(audio, volume, repeat) {
    if (volume == null) {
      volume = 1;
    }
    audio.volume = volume;
    return audio.play();
  };

  module.exports = function(renderer) {
    var chunk, overlay, player, stage, world;
    stage = new Container();
    world = new Container();
    overlay = new Container();
    Panzoom(renderer, world);
    stage.addChild(world);
    stage.addChild(overlay);
    player = null;
    chunk = null;
    loader.add([
      {
        name: "pika",
        url: "https://2.pixiecdn.com/sprites/137922/original.png?1"
      }, {
        name: "sheet",
        url: "https://danielx.whimsy.space/axe-blaster/platformertiles.png"
      }, {
        name: "map",
        url: "https://danielx.whimsy.space/axe-blaster/map.png"
      }, {
        name: "theme",
        url: "https://danielx.whimsy.space/axe-blaster/Theme to Red Ice.ogg"
      }, {
        name: "jump",
        url: "https://danielx.whimsy.space/axe-blaster/jump.wav"
      }
    ]).on("progress", function(_arg) {
      var progress;
      progress = _arg.progress;
    }).load(function() {
      chunk = MapChunk(loader.resources.sheet.texture, MapReader(loader.resources.map.texture.baseTexture.source));
      world.addChild(chunk);
      player = Player(loader.resources.pika.texture, loader.resources.jump.data);
      player.velocity.set(100, -1000);
      return world.addChild(player);
    });
    stage.update = function(dt, game) {
      if (player) {
        player.update(dt, chunk.children);
        world.pivot.x = player.x - (width / world.scale.x) / 2;
        world.pivot.y = player.y - (height / world.scale.y) / 2;
        return game.debugText("pivot: " + world.pivot + "\nscale: " + world.scale + "\n\nplayer.bounds: " + player.bounds + "\nplayer.velocity: " + player.velocity);
      }
    };
    return stage;
  };

}).call(this);

setup.content:
(function() {
  var ObservablePoint, Point, Rectangle, Stats, max, min, pointToString, styleNode;

  styleNode = document.createElement("style");

  styleNode.innerHTML = require('./style');

  document.head.appendChild(styleNode);

  Stats = require("./lib/stats.min");

  global.stats = new Stats;

  document.body.appendChild(stats.dom);

  ObservablePoint = PIXI.ObservablePoint, Point = PIXI.Point, Rectangle = PIXI.Rectangle;

  pointToString = function() {
    return "" + this.x + ", " + this.y;
  };

  ObservablePoint.prototype.toString = pointToString;

  Point.prototype.toString = pointToString;

  Rectangle.prototype.toString = function() {
    return "" + this.x + "," + this.y + "," + this.width + "," + this.height;
  };

  min = Math.min, max = Math.max;

  Math.clamp = function(number, lower, upper) {
    return max(min(number, upper), lower);
  };

}).call(this);

